package com.kn.NestedLoop;

public class NestedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=5;i++) {
			System.out.println("--> i = "+i);
			for(int j=1;j<=3;j++) {
				System.out.println("j = "+ j);
			}
		}

	}

}

	


